import React, { useState } from 'react';
import { useEffect } from 'react';
import { useSelector } from 'react-redux';
import listingApi from '../../../api/listingApi';
import Footer from '../../../components/Footer';
import Header from '../../../components/Header';
import Loading from '../../../components/Loading/Loading';
import BestCity from '../components/BestCity/BestCity';
import HowItWork from '../components/HowItWork/HowItWork';
import MainSearch from '../components/MainSearch/MainSearch';
import StatisticalSection from '../components/StatisticalSection/StatisticalSection';


Home.propTypes = {

};

const customStyles = {
    control: base => ({
        ...base,
        height: '51px',
        border: 'none',
        fontSize: '16px',
    })
};

function Home(props) {

    const loggedInUser = useSelector((state) => state.userSlice.current);
    const isLoggedIn = !!loggedInUser.id;
    const [loading, setLoading] = useState(true);
    const [citiesOption, setCitiesOption] = useState([]);
    const [listingType, setListingType] = useState([]);

    useEffect(() => {
        const fetchListingType = async () => {
            try {
                await listingApi.getOptionListingType().then(res => {
                    setListingType(res.data.data);
                })
            } catch (err) {
                console.log(err.message);
                setLoading(false);
            }
        }

        const fetchAllCity = async () => {
            try {
                await listingApi.getCity().then(res => {
                    setCitiesOption(res.data.data);

                })
            } catch (err) {
                console.log(err.message);
                setLoading(false);
            }
        }
        fetchListingType();
        fetchAllCity();
        window.scrollTo(0, 0);

        return () => {
            setCitiesOption([]);
            setListingType([]);
        }
    }, []);

    useEffect(() => {
        if (citiesOption.length > 0 && listingType.length > 0) {
            setLoading(false);
        }
    }, [citiesOption, listingType]);

    return (
        <div>
            {loading && <Loading />}
            <div id="page">
                <Header
                    loggedInUser={loggedInUser}
                    isLoggedIn={isLoggedIn}
                />
            </div>
            <MainSearch
                listingType={listingType}
                options={citiesOption}
                customStyles={customStyles}
            />

            <main>
                <BestCity
                    cities={cities}
                />
                <StatisticalSection />
                <HowItWork />
            </main>
        </div>
    );
}

export default Home;

